import React, { useEffect, useState } from 'react';
import { NotesProvider } from './contexts/NotesContext';
import { AIProvider } from './contexts/AIContext';
import UserAccountIcon from './components/UserAccountIcon';
import { useTheme } from './contexts/ThemeContext';
import Sidebar from './components/Sidebar';
import AIAssistant from './components/AIAssistant';
import CalendarPage from './pages/CalendarPage';

function App() {
  const [currentPage, setCurrentPage] = useState('home');

  // Setup keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (e) => {
      // Ctrl+N for new note
      if (e.ctrlKey && e.key === 'n') {
        e.preventDefault();
        document.dispatchEvent(new CustomEvent('newNote'));
      }

      // Ctrl+/ to toggle AI assistant
      if (e.ctrlKey && e.key === '/') {
        e.preventDefault();
        document.dispatchEvent(new CustomEvent('toggleAI'));
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, []);

  // Listen for navigation events
  useEffect(() => {
    const handleNavigation = (event) => {
      if (event.detail && event.detail.destination) {
        setCurrentPage(event.detail.destination);
      }
    };

    document.addEventListener('navigate', handleNavigation);
    return () => document.removeEventListener('navigate', handleNavigation);
  }, []);

  // Handle sidebar navigation clicks
  useEffect(() => {
    const handleCalendarClick = () => {
      setCurrentPage('calendar');
    };

    const handleHomeClick = () => {
      setCurrentPage('home');
    };

    const calendarLink = document.querySelector('a[href="#calendar"]');
    const homeLink = document.querySelector('a[href="#home"]');

    if (calendarLink) calendarLink.addEventListener('click', handleCalendarClick);
    if (homeLink) homeLink.addEventListener('click', handleHomeClick);

    return () => {
      if (calendarLink) calendarLink.removeEventListener('click', handleCalendarClick);
      if (homeLink) homeLink.removeEventListener('click', handleHomeClick);
    };
  }, []);

  // Render the appropriate page content based on currentPage
  const renderPageContent = () => {
    switch (currentPage) {
      case 'calendar':
        return <CalendarPage />;
      case 'home':
      default:
        return (
          <main className="flex-1 overflow-y-auto p-8">
            <div className="max-w-4xl mx-auto">
              <h1 className="text-4xl font-bold mb-12 text-center mt-12">What can I help with?</h1>

              {/* Search input */}
              <div className="relative mb-12">
                <input 
                  type="text" 
                  placeholder="Ask anything" 
                  className="w-full bg-gray-900 border border-gray-800 rounded-lg py-3 px-4 text-gray-200 focus:outline-none focus:ring-1 focus:ring-yellow-500"
                />
                <div className="absolute right-3 top-1/2 transform -translate-y-1/2 flex items-center space-x-2">
                  <span className="text-gray-500 text-sm">gemini-20</span>
                  <button className="text-gray-400 hover:text-white">
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                      <path fillRule="evenodd" d="M12.293 5.293a1 1 0 011.414 0l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414-1.414L14.586 11H3a1 1 0 110-2h11.586l-2.293-2.293a1 1 0 010-1.414z" clipRule="evenodd" />
                    </svg>
                  </button>
                </div>
              </div>

              {/* Filter tabs */}
              <div className="flex space-x-4 mb-8">
                <button className="px-4 py-1 rounded-full bg-gray-900 text-white">All</button>
                <button className="px-4 py-1 rounded-full text-gray-400 hover:bg-gray-900 hover:text-white">Myself</button>
                <button className="px-4 py-1 rounded-full text-yellow-500 hover:bg-gray-900">
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                    <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                  </svg>
                </button>
              </div>

              {/* Grid of notes */}
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {/* Note card examples */}
                <div className="bg-gray-900 rounded-lg overflow-hidden">
                  <div className="h-40 bg-gradient-to-r from-gray-800 to-gray-700 relative">
                    <img src="https://images.unsplash.com/photo-1511497584788-876760111969?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1489&q=80" className="w-full h-full object-cover opacity-50" alt="" />
                  </div>
                  <div className="p-4">
                    <h3 className="font-medium text-lg mb-1">Nokron</h3>
                    <p className="text-gray-400 text-sm">Elden Ring 'Zerin' razanımı ve olu senri</p>
                  </div>
                </div>

                <div className="bg-gray-900 rounded-lg overflow-hidden">
                  <div className="p-4">
                    <h3 className="font-medium text-lg mb-1">Kendine inanç</h3>
                    <p className="text-gray-400 text-sm">Kendine inanmaningücii ve manç geliştirme volian hakkinda bir yazii.</p>
                    <div className="mt-4">
                      <button className="text-gray-400 hover:text-white flex items-center text-sm">
                        <span>Votas</span>
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 ml-1" viewBox="0 0 20 20" fill="currentColor">
                          <path fillRule="evenodd" d="M7.293 14.707a1 1 0 010-1.414L10.586 10 7.293 6.707a1 1 0 011.414-1.414l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414 0z" clipRule="evenodd" />
                        </svg>
                      </button>
                    </div>
                  </div>
                </div>

                <div className="bg-gray-900 rounded-lg overflow-hidden flex">
                  <div className="p-4 flex-1">
                    <h3 className="font-medium text-lg mb-1">Kardeşime</h3>
                    <p className="text-gray-400 text-sm">Orhan Veli Kanık</p>
                  </div>
                  <div className="w-1/3">
                    <img src="https://images.unsplash.com/photo-1618005182384-a83a8bd57fbe?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1064&q=80" className="w-full h-full object-cover" alt="" />
                  </div>
                </div>
              </div>
            </div>
          </main>
        );
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-black text-gray-300">
      <NotesProvider>
        <AIProvider>
          {/* User Account Icon fixed at top-right */}
          <div className="absolute top-4 right-4 z-50">
            <UserAccountIcon />
          </div>
          <div className="flex flex-col md:flex-row h-screen">
            <Sidebar />
            <div className="flex-1 flex flex-col overflow-hidden">
              {renderPageContent()}
            </div>
            <AIAssistant />
          </div>
        </AIProvider>
      </NotesProvider>
    </div>
  );
}

export default App;
