import React, { useState, useEffect } from 'react';
import { useTheme } from '../contexts/ThemeContext';
import { useNotes } from '../contexts/NotesContext';

const Sidebar = () => {
  // Using darkMode for conditional styling if needed in the future
  const { darkMode } = useTheme();
  // Notes data available for future use
  const { notes } = useNotes();
  const [isOpen, setIsOpen] = useState(true);
  const [activePage, setActivePage] = useState('workspace');

  // Listen for navigation events to update active page
  useEffect(() => {
    const handleNavigation = (event) => {
      if (event.detail && event.detail.destination) {
        setActivePage(event.detail.destination);
      }
    };

    document.addEventListener('navigate', handleNavigation);
    return () => document.removeEventListener('navigate', handleNavigation);
  }, []);

  // Handle navigation click
  const handleNavClick = (page) => {
    setActivePage(page);
    document.dispatchEvent(new CustomEvent('navigate', { detail: { destination: page } }));
  };

  return (
    <>
      {/* Mobile sidebar toggle */}
      <button 
        className="md:hidden fixed top-4 left-4 z-20 p-2 rounded-md bg-black text-gray-300"
        onClick={() => setIsOpen(!isOpen)}
      >
        {isOpen ? (
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
          </svg>
        ) : (
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
          </svg>
        )}
      </button>

      {/* Sidebar */}
      <aside 
        className={`bg-black text-gray-300 w-[104px] transition-all duration-300 ease-in-out ${
          isOpen ? 'translate-x-0' : '-translate-x-full'
        } md:translate-x-0 fixed md:static inset-y-0 left-0 z-10 flex flex-col`}
      >
        {/* App Icon */}
        <div className="p-4 flex justify-center">
          <button className="p-2 rounded-md hover:bg-gray-800">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h7" />
            </svg>
          </button>
        </div>

        {/* Navigation */}
        <nav className="flex-1 flex flex-col items-center py-8 space-y-8">
          {/* Workspace */}
          <button 
            onClick={() => handleNavClick('workspace')} 
            className={`flex flex-col items-center justify-center w-full py-2 text-sm ${activePage === 'workspace' ? 'text-yellow-500 hover:text-yellow-400' : 'text-gray-300 hover:text-white'}`}
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mb-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" />
            </svg>
            <span>Home</span>
          </button>

          {/* Notes */}
          <button 
            onClick={() => handleNavClick('notes')} 
            className={`flex flex-col items-center justify-center w-full py-2 text-sm ${activePage === 'notes' ? 'text-yellow-500 hover:text-yellow-400' : 'text-gray-300 hover:text-white'}`}
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mb-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
            </svg>
            <span>Notes</span>
          </button>

          {/* Canvas */}
          <button 
            onClick={() => handleNavClick('canvas')} 
            className={`flex flex-col items-center justify-center w-full py-2 text-sm ${activePage === 'canvas' ? 'text-yellow-500 hover:text-yellow-400' : 'text-gray-300 hover:text-white'}`}
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mb-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
            </svg>
            <span>Canvas</span>
          </button>

          {/* To-Do */}
          <button 
            onClick={() => handleNavClick('todo')} 
            className={`flex flex-col items-center justify-center w-full py-2 text-sm ${activePage === 'todo' ? 'text-yellow-500 hover:text-yellow-400' : 'text-gray-300 hover:text-white'}`}
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mb-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
            </svg>
            <span>To-Do</span>
          </button>

          {/* Calendar */}
          <button 
            onClick={() => handleNavClick('calendar')} 
            className={`flex flex-col items-center justify-center w-full py-2 text-sm ${activePage === 'calendar' ? 'text-yellow-500 hover:text-yellow-400' : 'text-gray-300 hover:text-white'}`}
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mb-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
            </svg>
            <span>Calendar</span>
          </button>
        </nav>
      </aside>
    </>
  );
};

export default Sidebar;
